function showSum($input) {
	$inputArray = str_split($input);
	
	$result = 0;
	$sum = 0;
	foreach ($inputArray as $key => $stair) {
		$sum++;
		$result = ($stair == "(") ? $result + 1 : $result - 1;
		if ($result == -1) {
			return $sum;
		}
	}
	return 0;
}

